    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String pelicula = jTextField1.getText();
        boolean peliculaEncontrada = buscarPelicula(pelicula);
        if(!peliculaEncontrada) jComboBox1.addItem(pelicula); //si no se econtro la pelicula la agrega al comboBox
        else JOptionPane.showMessageDialog(null, "pelicula Existente"); // en casa contrario lanza una alerta
        jTextField1.setText("");
    }//GEN-LAST:event_jButton1ActionPerformed

    //busca si existe una pelicula en el comboBox
    private boolean buscarPelicula(String pelicula){
        int elementos = jComboBox1.getItemCount();
            for(int i = 0; i < elementos; i++){
                String item = String.valueOf(jComboBox1.getItemAt(i));
                if(item.equalsIgnoreCase(pelicula)){
                    return true;
                }
            }
        return false;
    }
    
    
